import React, { useState, useEffect } from 'react';
import { Container, TextField, Button, Typography, Grid, Paper, Rating } from '@mui/material';
import { getReviews, addReview } from './api';
import axios from 'axios';

const ReviewPage = () => {
  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState({
    bookName: '',
    UserName: '',
    rating: 0,
    genre: '',
  });








  useEffect(() => {
    // Fetch all reviews when the page loads
    getReviews()
      .then(response => setReviews(response.data))
      .catch(error => console.error('Error fetching reviews:', error));
  }, []);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNewReview({ ...newReview, [name]: value });
  };

  const handleRatingChange = (event, newValue) => {
    setNewReview({ ...newReview, rating: newValue });
  };

  const handleSubmit = () => {
    addReview(newReview)
      .then(response => {
        setReviews([...reviews, response.data]);
        setNewReview({ bookName: '', UserName: '', rating: 0, genre: '' });
      })
      .catch(error => console.error('Error adding review:', error));
  };

  return (
    <Container maxWidth="md" sx={{ paddingY: '20px' }}>
      <Typography variant="h4" gutterBottom>
        Reviews
      </Typography>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <Paper sx={{ padding: '16px' }}>
            <Typography variant="h6" gutterBottom>
              Add a New Review
            </Typography>
            <TextField
              label="Book Name"
              name="bookName"
              value={newReview.bookName}
              onChange={handleInputChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="User Name"
              name="UserName"
              value={newReview.UserName}
              onChange={handleInputChange}
              fullWidth
              margin="normal"
            />
            <Typography component="legend">Rating</Typography>
            <Rating
              name="rating"
              value={newReview.rating}
              onChange={handleRatingChange}
            />
            <TextField
              label="Genre"
              name="genre"
              value={newReview.genre}
              onChange={handleInputChange}
              fullWidth
              margin="normal"
            />
            <Button variant="contained" color="primary" onClick={handleSubmit}>
              Add Review
            </Button>
          </Paper>
        </Grid>

        <Grid item xs={12} sx={{ marginTop: '20px' }}>
          <Typography variant="h6" gutterBottom>
            All Reviews
          </Typography>
          {reviews.map((review, index) => (
            <Paper key={index} sx={{ padding: '16px', marginBottom: '10px' }}>
              <Typography variant="h6">{review.bookName}</Typography>
              <Typography variant="body1">By: {review.UserName}</Typography>
              <Rating value={review.rating} readOnly />
              <Typography variant="body2">Genre: {review.genre}</Typography>
            </Paper>
          ))}
        </Grid>
      </Grid>
    </Container>
  );
};

export default ReviewPage;
