import React, { useState, useEffect } from 'react';
import { Container, Typography, TextField, Button, Paper, Box, Alert, List, ListItem, ListItemText, IconButton, Dialog, DialogActions, DialogContent, DialogTitle } from '@mui/material';
import { Edit, Delete, Search, Add } from '@mui/icons-material';
import { getBooks, addBook, updateBook, deleteBook, getRecommendations, getReviews, addReview } from './api'; // Ensure path is correct

const BooksPage = () => {
  const [books, setBooks] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedBook, setSelectedBook] = useState(null);
  const [showDialog, setShowDialog] = useState(false);
  const [formValues, setFormValues] = useState({ title: '', author: '', description: '' });
  const [error, setError] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [recommendationError, setRecommendationError] = useState('');
  const [reviewInputs, setReviewInputs] = useState({});
  const [reviewError, setReviewError] = useState('');

  useEffect(() => {
    const fetchAllBooks = async () => {
      try {
        const response = await getBooks();
        setBooks(response.data);
      } catch (error) {
        setError('Failed to fetch books');
      }
    };

    fetchAllBooks();
  }, []);

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const response = await getBooks();
      setBooks(response.data.filter(book => book.title.toLowerCase().includes(searchQuery.toLowerCase())));
    } catch (error) {
      setError('Failed to search books');
    }
  };

  const handleAddOrUpdateBook = async (e) => {
    e.preventDefault();
    try {
      if (selectedBook) {
        await updateBook(selectedBook._id, formValues);
      } else {
        await addBook(formValues);
      }
      setShowDialog(false);
      setFormValues({ title: '', author: '', description: '' });
      setSelectedBook(null);
      // Refresh the book list after adding or updating
      const response = await getBooks();
      setBooks(response.data);
    } catch (error) {
      setError('Failed to add or update book');
    }
  };

  const handleDeleteBook = async (bookId) => {
    try {
      await deleteBook(bookId);
      // Refresh the book list after deletion
      const response = await getBooks();
      setBooks(response.data);
    } catch (error) {
      setError('Failed to delete book');
    }
  };

  const openDialog = (book = null) => {
    if (book) {
      setSelectedBook(book);
      setFormValues({ title: book.title, author: book.author, description: book.description });
    } else {
      setSelectedBook(null);
      setFormValues({ title: '', author: '', description: '' });
    }
    setShowDialog(true);
  };

  const handleGetRecommendations = async () => {
    try {
      const response = await getRecommendations({
        readingHistory: books.map(book => book.title),
      });
      setRecommendations(response.data.recommendations);
      setRecommendationError('');
    } catch (error) {
      setRecommendationError('Failed to get recommendations');
    }
  };

  const handleReviewInputChange = (bookId, value) => {
    setReviewInputs(prevInputs => ({
      ...prevInputs,
      [bookId]: value
    }));
  };

  const handleAddReview = async (bookId) => {
    try {
      console.log(`Adding review for bookId ${bookId} with input: ${reviewInputs[bookId]}`); // Debugging statement
      await addReview(bookId, { rating: 5, comment: reviewInputs[bookId] }); // Example rating, adjust as needed
      setReviewInputs(prevInputs => ({
        ...prevInputs,
        [bookId]: ''
      }));
      // Refresh reviews for the selected book
      fetchReviews(bookId);
    } catch (error) {
      console.error('Failed to add review', error); // Debugging statement
      setReviewError('Failed to add review');
    }
  };
  
  const fetchReviews = async (bookId) => {
    try {
      const response = await getReviews(bookId);
      setBooks(prevBooks =>
        prevBooks.map(book =>
          book._id === bookId
            ? { ...book, reviews: response.data }
            : book
        )
      );
    } catch (error) {
      console.error('Failed to fetch reviews', error); // Debugging statement
      setReviewError('Failed to fetch reviews');
    }
  };

  return (
    <Container maxWidth="md" sx={{ paddingY: '20px' }}>
      
      <Paper sx={{ padding: '40px', marginBottom: '20px' }}>
        <Typography 
          variant="h4" 
          component="h1"
          gutterBottom 
          sx={{ 
            fontWeight: 'bold', 
            color: '#3e2723', 
            fontSize: '2rem', 
            textAlign: 'center', 
            marginBottom: '20px'
          }}
        >
          Books Collection
        </Typography>
        <form onSubmit={handleSearch}>
          <TextField
            label="Search by Title"
            variant="outlined"
            fullWidth
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            sx={{ marginBottom: '10px' }}
          />
          <Box sx={{ display: 'flex', justifyContent: 'center' }}>
            <Button 
              type="submit" 
              variant="contained" 
              color="primary"
              startIcon={<Search />}
              sx={{ paddingX: 5, backgroundColor: '#8d6e63', '&:hover': { backgroundColor: '#6d4c41' } }}
            >
              Search
            </Button>
          </Box>
        </form>
      </Paper>

      {error && <Alert severity="error" sx={{ marginBottom: '20px' }}>{error}</Alert>}

      <Paper sx={{ padding: '40px', marginBottom: '20px' }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Typography 
            variant="h6" 
            gutterBottom
            sx={{ 
              fontWeight: 'bold', 
              color: '#3e2723', 
              fontSize: '1.5rem',
              textAlign: 'center', 
              marginBottom: '20px'
            }}
          >
            Book List
          </Typography>
          <Button 
            variant="contained" 
            color="primary" 
            onClick={() => openDialog()} 
            startIcon={<Add />} // Add "+" icon
            sx={{ paddingX: 5, backgroundColor: '#8d6e63', '&:hover': { backgroundColor: '#6d4c41' } }}
          >
            Add New Book
          </Button>
        </Box>
        <List>
          {books.map((book) => (
            <ListItem key={book._id} divider>
              <ListItemText 
                primary={book.title} 
                secondary={
                  <React.Fragment>
                    <span>Author: {book.author}</span>
                    <br />
                    <span>Description: {book.description}</span>
                  </React.Fragment>
                }
              />
              <IconButton onClick={() => openDialog(book)}>
                <Edit />
              </IconButton>
              <IconButton onClick={() => handleDeleteBook(book._id)}>
                <Delete />
              </IconButton>
              <Box sx={{ width: '100%' }}>
                <TextField
                  label="Add a Review"
                  variant="outlined"
                  fullWidth
                  multiline
                  rows={2}
                  value={reviewInputs[book._id] || ''}
                  onChange={(e) => handleReviewInputChange(book._id, e.target.value)}
                  sx={{ marginBottom: '10px' }}
                />
                <Button 
                  variant="contained" 
                  color="primary" 
                  onClick={() => handleAddReview(book._id)}
                  sx={{ paddingX: 5, backgroundColor: '#8d6e63', '&:hover': { backgroundColor: '#6d4c41' } }}
                >
                  Add Review
                </Button>
                {reviewError && <Alert severity="error" sx={{ marginTop: '10px' }}>{reviewError}</Alert>}
                <Box sx={{ marginTop: '20px' }}>
                  {book.reviews && book.reviews.map((review, index) => (
                    <Paper key={index} sx={{ padding: '10px', marginBottom: '10px' }}>
                      <Typography variant="subtitle1" component="div">
                        Rating: {review.rating}
                      </Typography>
                      <Typography variant="body2" component="div">
                        Comment: {review.comment}
                      </Typography>
                    </Paper>
                  ))}
                </Box>
              </Box>
            </ListItem>
          ))}
        </List>
      </Paper>

      <Dialog open={showDialog} onClose={() => setShowDialog(false)}>
        <DialogTitle>{selectedBook ? 'Edit Book' : 'Add New Book'}</DialogTitle>
        <DialogContent>
          <TextField
            label="Title"
            variant="outlined"
            fullWidth
            margin="dense"
            value={formValues.title}
            onChange={(e) => setFormValues({ ...formValues, title: e.target.value })}
          />
          <TextField
            label="Author"
            variant="outlined"
            fullWidth
            margin="dense"
            value={formValues.author}
            onChange={(e) => setFormValues({ ...formValues, author: e.target.value })}
          />
          <TextField
            label="Description"
            variant="outlined"
            fullWidth
            multiline
            rows={4}
            margin="dense"
            value={formValues.description}
            onChange={(e) => setFormValues({ ...formValues, description: e.target.value })}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setShowDialog(false)}>Cancel</Button>
          <Button onClick={handleAddOrUpdateBook} color="primary">
            {selectedBook ? 'Update' : 'Add'}
          </Button>
        </DialogActions>
      </Dialog>

      <Paper sx={{ padding: '20px', marginTop: '20px' }}>
        <Typography 
          variant="h6" 
          gutterBottom
          sx={{ 
            fontWeight: 'bold', 
            color: '#3e2723', 
            fontSize: '1.5rem',
            textAlign: 'center', 
            marginBottom: '20px'
          }}
        >
          Get Recommendations
        </Typography>
        <Box sx={{ display: 'flex', justifyContent: 'center' }}>
          <Button 
            variant="contained" 
            color="primary" 
            onClick={handleGetRecommendations}
            sx={{ paddingX: 5, backgroundColor: '#8d6e63', '&:hover': { backgroundColor: '#6d4c41' } }}
          >
            Get Recommendations
          </Button>
        </Box>
        {recommendationError && <Alert severity="error" sx={{ marginTop: '30px' }}>{recommendationError}</Alert>}
        {recommendations.length > 0 && (
          <Paper sx={{ padding: '20px', marginTop: '20px' }}>
            <Typography variant="h6" gutterBottom>Recommended Books</Typography>
            <List>
              {recommendations.map((rec, index) => (
                <ListItem key={index} divider>
                  <ListItemText primary={rec} />
                </ListItem>
              ))}
            </List>
          </Paper>
        )}
      </Paper>
    </Container>
  );
};

export default BooksPage;
