import React, { useState, useEffect } from 'react';
import { Container, TextField, Button, Typography, Grid, Paper, Rating, Box } from '@mui/material';
import { getReviews, addReview } from './api';
import axios from 'axios';

const ReviewPage = () => {
  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState({
    bookName: '',
    UserName: '',
    rating: 0,
    genre: '',
  });

  useEffect(() => {
    // Fetch all reviews when the page loads
    getReviews()
      .then(response => setReviews(response.data))
      .catch(error => console.error('Error fetching reviews:', error));
  }, []);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNewReview({ ...newReview, [name]: value });
  };

  const handleRatingChange = (event, newValue) => {
    setNewReview({ ...newReview, rating: newValue });
  };

  const handleSubmit = () => {
    addReview(newReview)
      .then(response => {
        setReviews([...reviews, response.data]);
        setNewReview({ bookName: '', UserName: '', rating: 0, genre: '' });
      })
      .catch(error => console.error('Error adding review:', error));
  };

  return (
    <Container maxWidth="md" sx={{ paddingY: '20px' }}>
      
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <Paper sx={{ padding: '16px' }}>

          <Typography 
          variant="h4" 
          component="h1"
          gutterBottom 
          sx={{ 
            fontWeight: 'bold', 
            color: '#3e2723', 
            fontSize: '2rem', 
            textAlign: 'center', 
            marginBottom: '20px'
          }}
        >
            Reviews
        </Typography>

        <Typography 
            variant="h6" 
            gutterBottom
            sx={{ 
              fontWeight: 'bold', 
              color: '#3e2723', 
              fontSize: '1.5rem',
              marginBottom: '20px'
            }}
          >
            Add a Review
          </Typography>
            <TextField
              label="Book Name"
              name="bookName"
              value={newReview.bookName}
              onChange={handleInputChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Your Name"
              name="YourName"
              value={newReview.UserName}
              onChange={handleInputChange}
              fullWidth
              margin="normal"
            />
            <Typography component="legend">Rating</Typography>
            <Rating
              name="rating"
              value={newReview.rating}
              onChange={handleRatingChange}
            />
            <TextField
              label="Genre"
              name="genre"
              value={newReview.genre}
              onChange={handleInputChange}
              fullWidth
              margin="normal"
            />
            <Box 
            sx={{ 
            display: 'flex', 
            justifyContent: 'center', 
            marginTop: 2,
            marginBottom: 5,// Adjust as needed for spacing from top
            }}
            >
            <Button 
            variant="contained" 
            color="primary"  
            onClick={handleSubmit} 
            sx={{ 
            paddingX: 5, 
            backgroundColor: '#8d6e63', 
            '&:hover': { backgroundColor: '#6d4c41' } 
            }}
            >
            Add Review
                </Button>
            </Box>
          </Paper>
        


        <Paper sx={{ padding: '40px', marginBottom: '20px' }}>
    
         
      <Typography variant="h6" sx={{ 
            fontWeight: 'bold', 
            color: '#3e2723', 
            textAlign: 'center', 
            marginBottom: '20px'
          }}>
        Invite Our Discord Bot to Your Server
      </Typography>
      <Box sx={{ display: 'flex', justifyContent: 'center' }}>
      <Button
        variant="contained"
        color="primary"
        href="https://discord.com/oauth2/authorize?client_id=1280239916034429093&permissions=8&integration_type=0&scope=bot"
        target="_blank"
        sx={{ paddingX: 5, backgroundColor: '#8d6e63', '&:hover': { backgroundColor: '#6d4c41' } }}
      >
        Invite Bot
      </Button>
      </Box> 

      <Typography variant="h6" style={{ marginTop: '40px' }}>
        After Adding The Bot To Your Discord Server

      </Typography>

      <Typography variant="body1">
        <ul>
          <li><strong>@book-recomendation-app</strong> - Please remember to mention the bot before using any commands to ensure it receives your request.</li>
          </ul>
      </Typography>

      <Typography variant="h6" style={{ marginTop: '20px' }}>
        How The Use Commands To Bot

      </Typography>
      <Typography variant="body1">
        <ul>
          <li><strong>!addbook "Title" "Author" "Description"</strong> - Add a book to your library.</li>
          <li><strong>!listbooks</strong> - List all books in your library.</li>
          <li><strong>!editbook "Current Title" "New Title" "New Author" "New Description"</strong> - Edit a book in your library.</li>
          <li><strong>!deletebook "Title"</strong> - Delete a book from your library.</li>
        </ul>
      </Typography>

      <Typography variant="h6" style={{ marginTop: '20px' }}>
        Example :- <strong>@book-recomendation-app !addbook "Title" "Author" "Description"</strong>

      </Typography>
    </Paper>

    </Grid>

        <Grid item xs={12} sx={{ marginTop: '20px' }}>
        <Paper sx={{ padding: '16px' }}>
        <Typography 
            variant="h6" 
            gutterBottom
            sx={{ 
              fontWeight: 'bold', 
              color: '#3e2723', 
              fontSize: '1.5rem',
              marginBottom: '20px'
            }}
          >
            All Reviews
          </Typography>
          {reviews.map((review, index) => (
            <Paper 
            key={index} 
            sx={{ 
              padding: '16px', 
              marginBottom: '10px', 
              backgroundColor: 'rgba(166, 127, 116, 0.3)' // Light brown with 80% opacity
            }}
          >
              <Typography variant="h6">{review.bookName}</Typography>
              <Typography variant="body1">By: {review.UserName}</Typography>
              <Rating value={review.rating} readOnly />
              <Typography variant="body2">Genre: {review.genre}</Typography>
            </Paper>
          ))}
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default ReviewPage;
