import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

// Set up axios defaults
const instance = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Authentication
export const signup = (email, password) => {
  return instance.post('/auth/signup', { email, password });
};

export const login = (email, password) => {
  return instance.post('/auth/login', { email, password });
};

// Books
export const getBooks = () => {
  return instance.get('/books');
};

export const getBook = (id) => {
  return instance.get(`/books/${id}`);
};

export const addBook = (book) => {
  return instance.post('/books', book);
};

export const updateBook = (id, book) => {
  return instance.put(`/books/${id}`, book);
};

export const deleteBook = (id) => {
  return instance.delete(`/books/${id}`);
};

// Reviews
export const getReviews = () => {
  return instance.get('/reviews');
};

export const addReview = (review) => {
  return instance.post('/reviews', review);
};
