import React, { useState, useEffect } from 'react';
import { Container, Grid, Typography, TextField, Button, Paper, Box, Alert, List, ListItem, ListItemText, IconButton, Modal, Dialog, DialogActions, DialogContent, DialogTitle } from '@mui/material';
import { Edit, Delete, Search, Add } from '@mui/icons-material';
import { getBooks, addBook, updateBook, deleteBook, getRecommendations, getReviews, addReview } from './api'; // Ensure path is correct
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import BookIcon from '@mui/icons-material/Book';
import StarIcon from '@mui/icons-material/Star';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import { SvgIcon } from '@mui/material';

const DiscordIcon = (props) => (
  <SvgIcon {...props}>
    <path d="M22 24H2C0.9 24 0 23.1 0 22V2C0 0.9 0.9 0 2 0H22C23.1 0 24 0.9 24 2V22C24 23.1 23.1 24 22 24ZM15.6 17.6C15.6 17.6 15.3 17.1 15 16.6C17.2 16.1 17.8 14.6 17.8 14.6C17.3 14.9 16.8 15.1 16.3 15.2C16.2 15.2 16.1 15.2 16 15.2C15.9 15.2 15.7 15.2 15.6 15.2C15.3 15.2 15 15.1 14.7 15C14.4 14.9 14.1 14.7 13.9 14.4C13.9 14.4 13.9 14.4 13.9 14.4C14.3 14.2 14.6 14 14.8 13.8C14.5 13.7 14.2 13.6 14 13.4C13.8 13.2 13.5 13.1 13.3 13C13.1 12.9 12.9 12.8 12.7 12.8C12.5 12.7 12.4 12.7 12.3 12.6C12.1 12.5 12 12.5 11.8 12.4C11.7 12.4 11.6 12.4 11.4 12.3C11.2 12.3 11.1 12.3 11 12.2C10.9 12.2 10.7 12.2 10.6 12.2C10.5 12.1 10.4 12.1 10.2 12.1C10.1 12 10 12 9.8 12H9.6C9.5 12 9.3 12 9.2 12.1C9.1 12.1 9 12.1 8.9 12.2C8.8 12.2 8.6 12.2 8.5 12.3C8.4 12.3 8.3 12.3 8.2 12.4C8.1 12.4 7.9 12.4 7.8 12.5C7.7 12.5 7.6 12.5 7.5 12.6C7.3 12.7 7.1 12.7 6.9 12.8C6.7 12.9 6.6 13 6.4 13.1C6.2 13.2 6 13.3 5.8 13.4C5.7 13.5 5.5 13.6 5.4 13.8C5.3 13.9 5.2 14 5.1 14.2C5 14.4 4.9 14.5 4.8 14.7C4.8 14.7 5.4 16.2 7.6 16.6C7.3 17.1 7 17.6 7 17.6C5.2 17.5 3.9 16.6 3.4 14.8C3.4 14.8 4.3 15.4 5.6 15.6C6.2 15.6 6.8 15.4 7.4 15.2C7.4 15.2 7.6 15.1 7.7 15.1C7.8 15.1 7.9 15.1 8 15C8.1 15 8.3 14.9 8.4 14.8C8.5 14.8 8.6 14.7 8.7 14.7C8.9 14.6 9.1 14.5 9.2 14.5C9.4 14.4 9.5 14.4 9.7 14.4C9.8 14.3 10 14.3 10.1 14.3C10.3 14.3 10.5 14.2 10.6 14.2C10.8 14.2 10.9 14.2 11.1 14.2C11.2 14.2 11.4 14.2 11.6 14.3C11.7 14.3 11.9 14.3 12.1 14.4C12.2 14.4 12.4 14.4 12.5 14.5C12.6 14.5 12.7 14.6 12.9 14.7C13 14.7 13.1 14.8 13.3 14.8C13.4 14.9 13.5 14.9 13.7 15C13.8 15 13.9 15.1 14 15.1C14.1 15.1 14.2 15.1 14.3 15.2C14.4 15.2 14.5 15.2 14.6 15.2C14.7 15.2 14.9 15.2 15 15.2C15.1 15.2 15.2 15.2 15.3 15.2C15.4 15.2 15.6 15.2 15.7 15.2C16.3 15.1 16.8 14.9 17.3 14.6C17.3 14.6 16.8 16.1 14.6 16.6C14.3 17.1 14 17.6 14 17.6C14 17.6 15 17.6 15.6 17.6Z" />
  </SvgIcon>
);


const BooksPage = () => {
  const [books, setBooks] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedBook, setSelectedBook] = useState(null);
  const [showDialog, setShowDialog] = useState(false);
  const [formValues, setFormValues] = useState({ title: '', author: '', description: '' });
  const [error, setError] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [recommendationError, setRecommendationError] = useState('');
  const [readingHistory, setReadingHistory] = useState('');
  const [reviews, setReviews] = useState([]);
  const [reviewError, setReviewError] = useState('');
  const [newReview, setNewReview] = useState('');

  const inviteLink = `https://discord.com/oauth2/authorize?client_id=1280239916034429093&permissions=8&integration_type=0&scope=bot`;


  const navigate = useNavigate();

  const handleSeeReviews = () => {
    navigate('/reviews'); // Go to review page
  };


  useEffect(() => {
    const fetchAllBooks = async () => {
      try {
        const response = await getBooks();
        setBooks(response.data);
      } catch (error) {
        setError('Failed to fetch books');
      }
    };

    fetchAllBooks();
  }, []);

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const response = await getBooks();
      setBooks(response.data.filter(book => book.title.toLowerCase().includes(searchQuery.toLowerCase())));
    } catch (error) {
      setError('Failed to search books');
    }
  };

  const handleAddOrUpdateBook = async (e) => {
    e.preventDefault();
    try {
      if (selectedBook) {
        await updateBook(selectedBook._id, formValues);
      } else {
        await addBook(formValues);
      }
      setShowDialog(false);
      setFormValues({ title: '', author: '', description: '' });
      setSelectedBook(null);
      // Refresh the book list after adding or updating
      const response = await getBooks();
      setBooks(response.data);
    } catch (error) {
      setError('Failed to add or update book');
    }
  };

  const handleDeleteBook = async (bookId) => {
    try {
      await deleteBook(bookId);
      // Refresh the book list after deletion
      const response = await getBooks();
      setBooks(response.data);
    } catch (error) {
      setError('Failed to delete book');
    }
  };

  const openDialog = (book = null) => {
    if (book) {
      setSelectedBook(book);
      setFormValues({ title: book.title, author: book.author, description: book.description });
    } else {
      setSelectedBook(null);
      setFormValues({ title: '', author: '', description: '' });
    }
    setShowDialog(true);
  };

  const handleGetRecommendations = async () => {
    try {
      const response = await getRecommendations({
        readingHistory: books.map(book => book.title),
      });
      
      setRecommendations(response.data.recommendations);
      setRecommendationError('');
    } catch (error) {
      setRecommendationError('Failed to get recommendations');
    }
  };

  const handleAddReview = async (e) => {
    e.preventDefault();
    try {
      await addReview(selectedBook._id, { content: newReview });
      setNewReview('');
      fetchReviews(selectedBook._id);
    } catch (error) {
      setReviewError('Failed to add review');
    }
  };

  const fetchReviews = async (bookId) => {
    try {
      const response = await getReviews(bookId);
      setReviews(response.data);
    } catch (error) {
      setReviewError('Failed to fetch reviews');
    }
  };


  return (
  
    <Container maxWidth="ls">
      <Grid maxWidth="md"
      sx={{
        paddingY: '20px',
        marginLeft: 'auto', // Align the container to the left side
        marginRight: 'auto',
      }}>


      <Paper sx={{ padding: '40px', marginBottom: '20px' }}>
        <Typography 
          variant="h4" 
          component="h1"
          gutterBottom 
          sx={{ 
            fontWeight: 'bold', 
            color: '#3e2723', 
            fontSize: '2rem', 
            textAlign: 'center', 
            marginBottom: '20px'
          }}
        >
          Books Collection
        </Typography>
        <form onSubmit={handleSearch}>
          <TextField
            label="Search by Title"
            variant="outlined"
            fullWidth
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            sx={{ marginBottom: '10px' }}
          />
          <Box sx={{ display: 'flex', justifyContent: 'center' }}>
            <Button 
              type="submit" 
              variant="contained" 
              color="primary"
              startIcon={<Search />}
              sx={{ paddingX: 5, backgroundColor: '#8d6e63', '&:hover': { backgroundColor: '#6d4c41' } }}
            >
              Search
            </Button>
          </Box>
        </form>
        
      </Paper>

      {error && <Alert severity="error" sx={{ marginBottom: '20px' }}>{error}</Alert>}




      <Paper sx={{ padding: '40px', marginBottom: '20px' }}>
    
         
      <Typography variant="h6" sx={{ 
            fontWeight: 'bold', 
            color: '#3e2723', 
            textAlign: 'center', 
            marginBottom: '20px'
          }}>
        Invite Our Discord Bot to Your Server
      </Typography>
      <Box sx={{ display: 'flex', justifyContent: 'center' }}>
      <Button
        variant="contained"
        color="primary"
        href="https://discord.com/oauth2/authorize?client_id=1280239916034429093&permissions=8&integration_type=0&scope=bot"
        target="_blank"
        startIcon={<DiscordIcon/>}
        sx={{ paddingX: 5, backgroundColor: '#8d6e63', '&:hover': { backgroundColor: '#6d4c41' } }}
      >
        Invite Bot
      </Button>
      </Box> 

      <Typography variant="h6" style={{ marginTop: '40px' }}>
        After Adding The Bot To Your Discord Server

      </Typography>

      <Typography variant="body1">
        <ul>
          <li><strong>@book-recomendation-app</strong> - Please remember to mention the bot before using any commands to ensure it receives your request.</li>
          </ul>
      </Typography>

      <Typography variant="h6" style={{ marginTop: '20px' }}>
        How The Use Commands To Bot

      </Typography>
      <Typography variant="body1">
        <ul>
          <li><strong>!addbook "Title" "Author" "Description"</strong> - Add a book to your library.</li>
          <li><strong>!listbooks</strong> - List all books in your library.</li>
          <li><strong>!editbook "Current Title" "New Title" "New Author" "New Description"</strong> - Edit a book in your library.</li>
          <li><strong>!deletebook "Title"</strong> - Delete a book from your library.</li>
        </ul>
      </Typography>

      <Typography variant="h6" style={{ marginTop: '20px' }}>
        Example :- <strong>@book-recomendation-app !addbook "Title" "Author" "Description"</strong>

      </Typography>
    </Paper>

    <Paper sx={{ padding: '40px', marginBottom: '20px' }}>
    
    <Typography variant="h6" sx={{ 
            fontWeight: 'bold', 
            color: '#3e2723', 
            textAlign: 'center', 
            marginBottom: '20px',
            fontSize: '1.1rem'
          }}>
        "Explore and share your thoughts on the books you love. Discover reviews and contribute your own!"
      </Typography>
      <Box sx={{ display: 'flex', justifyContent: 'center' }}>
      <Button
      onClick={handleSeeReviews}
      variant="contained"
      color="primary"
      endIcon={<ChatBubbleOutlineIcon />}
      sx={{ paddingX: 5, backgroundColor: '#8d6e63', '&:hover': { backgroundColor: '#6d4c41' } }}>
            See Reviews
          </Button>
          </Box>
          </Paper>

      <Paper sx={{ padding: '40px', marginBottom: '20px' }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Typography 
            variant="h6" 
            gutterBottom
            sx={{ 
              fontWeight: 'bold', 
              color: '#3e2723', 
              fontSize: '1.5rem',
              textAlign: 'center', 
              marginBottom: '20px'
            }}
          >
            Book List
          </Typography>
          <Button 
            variant="contained" 
            color="primary" 
            onClick={() => openDialog()} 
            startIcon={<Add />} // Add "+" icon
            sx={{ paddingX: 5, backgroundColor: '#8d6e63', '&:hover': { backgroundColor: '#6d4c41' } }}
          >
            Add New Book
          </Button>
         
        </Box>
        <List>
          {books.map((book) => (
            <ListItem key={book._id} divider>
              <ListItemText 
                primary={book.title} 
                secondary={
                  <React.Fragment>
                    <span>Author: {book.author}</span>
                    <br />
                    <span>Description: {book.description}</span>
                  </React.Fragment>
                }
              />
              <IconButton onClick={() => openDialog(book)}>
                <Edit />
              </IconButton>
              <IconButton onClick={() => handleDeleteBook(book._id)}>
                <Delete />
              </IconButton>
            </ListItem>
          ))}
        </List>
      </Paper>

      

      <Paper sx={{ padding: '40px', marginBottom: '20px' }}>
        <Typography 
          variant="h6" 
          gutterBottom
          sx={{ 
            fontWeight: 'bold', 
            color: '#3e2723', 
            fontSize: '1.5rem', 
            textAlign: 'center', 
            marginBottom: '20px'
          }}
        >
          Get Recommendations
        </Typography>
        <Box sx={{ display: 'flex', justifyContent: 'center' }}>
          
          <Button 
            variant="contained" 
            color="primary" 
            onClick={handleGetRecommendations}
            sx={{ paddingX: 5, backgroundColor: '#8d6e63', '&:hover': { backgroundColor: '#6d4c41' } }}
          >
            Get Recommendations
          </Button>
        </Box>
        {recommendationError && <Alert severity="error" sx={{ marginTop: '30px' }}>{recommendationError}</Alert>}
        {recommendations.length > 0 && (
          <Paper sx={{ padding: '20px', marginTop: '20px' }}>
            <Typography variant="h6" gutterBottom>Recommended Books</Typography>
            <List>
              {recommendations.map((rec, index) => (
                <ListItem key={index} divider>
                  <ListItemText primary={rec} />
                </ListItem>
              ))}
            </List>
          </Paper>
        )}
        <Typography 
          variant="h6" 
          gutterBottom
          sx={{ 
            fontWeight: 'bold', 
            color: '#3e2723', 
            fontSize: '1rem', 
            textAlign: 'center', 
            marginBottom: '20px',
            marginTop: '20px'
          }}
        >
          Or
        </Typography>
        <TextField
            label="Enter keyword to get recommendation"
            variant="outlined"
            multiline
            rows={4}
            fullWidth
            value={readingHistory}
            onChange={(e) => setReadingHistory(e.target.value)}
            sx={{ marginBottom: '10px' }}
          />
      </Paper>

      <Modal
        open={showDialog}
        onClose={() => setShowDialog(false)}
        aria-labelledby="modal-title"
        aria-describedby="modal-description"
      >
        <Paper 
          elevation={4} 
          sx={{ 
            padding: '20px', 
            borderRadius: 2, 
            width: '80%',
            maxWidth: '500px',
            margin: 'auto',
            marginTop: '10%',
          }}
        >
          <DialogTitle id="modal-title">{selectedBook ? 'Update Book' : 'Add New Book'}</DialogTitle>
          <DialogContent>
            <form onSubmit={handleAddOrUpdateBook}>
              <TextField
                label="Title"
                variant="outlined"
                fullWidth
                required
                value={formValues.title}
                onChange={(e) => setFormValues({ ...formValues, title: e.target.value })}
                sx={{ marginBottom: '20px' }}
              />
              <TextField
                label="Author"
                variant="outlined"
                fullWidth
                required
                value={formValues.author}
                onChange={(e) => setFormValues({ ...formValues, author: e.target.value })}
                sx={{ marginBottom: '20px' }}
              />
              <TextField
                label="Description"
                variant="outlined"
                fullWidth
                multiline
                rows={4}
                value={formValues.description}
                onChange={(e) => setFormValues({ ...formValues, description: e.target.value })}
                sx={{ marginBottom: '20px' }}
              />
              <DialogActions>
                <Button 
                  type="submit" 
                  variant="contained" 
                  color="primary"
                >
                  {selectedBook ? 'Update' : 'Add'}
                </Button>
                <Button 
                  onClick={() => setShowDialog(false)}
                  color="secondary"
                >
                  Cancel
                </Button>
              </DialogActions>
            </form>
          </DialogContent>
        </Paper>
      </Modal>
      </Grid>
    </Container>
    


  



  );
};

export default BooksPage;
