import React, { useState } from 'react';
import { Container, Typography, Paper, Box, Button } from '@mui/material';

const HomePage = () => {
  const [showLogin, setShowLogin] = useState(false);

  const handleStartHereClick = () => {
    setShowLogin(true);
  };

  return (
    <Container
      maxWidth="lg"
      sx={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        backgroundColor: '#e0e0e0', // Light grey background for the page
        position: 'relative',
      }}
    >
      {/* Left side: Home Page */}
      <Paper
        elevation={3}
        sx={{
          padding: 4,
          backgroundColor: 'rgba(255, 255, 255, 0.95)',
          width: showLogin ? '50%' : '80%',
          transition: 'width 0.5s ease',
        }}
      >
        <Box textAlign="center">
          <Typography variant="h3" component="h1" gutterBottom>
            BookNest
          </Typography>
          <Typography variant="body1" color="textSecondary">
            Welcome to the Book Recommendation System!
          </Typography>
          <Button
            variant="contained"
            color="primary"
            sx={{ marginTop: 4 }}
            onClick={handleStartHereClick}
          >
            Start Here
          </Button>
        </Box>
      </Paper>

      {/* Right side: Login Page (conditional rendering) */}
      {showLogin && (
        <Paper
          elevation={3}
          sx={{
            padding: 4,
            backgroundColor: 'rgba(255, 255, 255, 0.95)',
            width: '50%',
            marginLeft: 2,
            transition: 'width 0.5s ease',
          }}
        >
          <Box textAlign="center">
            <Typography variant="h4" component="h2" gutterBottom>
              Login Page
            </Typography>
            {/* Add your login form elements here */}
            <Typography variant="body1" color="textSecondary">
              Please enter your credentials to log in.
            </Typography>
          </Box>
        </Paper>
      )}
    </Container>
  );
};

export default HomePage;
