import React from 'react';
import { Container, Typography, Button, Box, Paper } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const quotes = [
  "“A room without books is like a body without a soul.” – Marcus Tullius Cicero",
  "“So many books, so little time.” – Frank Zappa",
  "“The only thing you absolutely have to know is the way to the library.” – Albert Einstein",
  "“Books are a uniquely portable magic.” – Stephen King"
];

const getRandomPosition = () => {
  const x = Math.floor(Math.random() * 80) + 10; // 10 to 90 percent
  const y = Math.floor(Math.random() * 80) + 10; // 10 to 90 percent
  return { x: `${x}%`, y: `${y}%` };
};

const HomePage = () => {
  const navigate = useNavigate();

  const handleStartClick = () => {
    navigate('/login');
  };

  return (
    <Container maxWidth="md" sx={{ height: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center', position: 'relative' }}>
      <Paper
        elevation={4}
        sx={{
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          padding: '40px',
          textAlign: 'center',
          borderRadius: 2,
          position: 'relative',
          backgroundColor: '#f5f5f5',
          zIndex: 1,
          '&:before, &:after': {
            content: '""',
            position: 'absolute',
            top: 0,
            bottom: 0,
            width: '15px',
            backgroundColor: '#d7ccc8',
            zIndex: -1,
          },
          '&:before': {
            left: '-15px',
            borderTopLeftRadius: 8,
            borderBottomLeftRadius: 8,
          },
          '&:after': {
            right: '-15px',
            borderTopRightRadius: 8,
            borderBottomRightRadius: 8,
          },
        }}
      >
        <Typography variant="h3" component="h1" gutterBottom sx={{ fontWeight: 'bold', color: '#3e2723' }}>
          Welcome to BookNest
        </Typography>
        <Typography variant="h6" component="p" gutterBottom sx={{ marginBottom: '20px', color: '#5d4037' }}>
          Your personal book assistant
        </Typography>
        <Button 
          variant="contained" 
          color="primary" 
          size="large" 
          onClick={handleStartClick}
          sx={{ paddingX: 5, backgroundColor: '#8d6e63', '&:hover': { backgroundColor: '#6d4c41' } }}
        >
          Start Here
        </Button>
      </Paper>

      {quotes.map((quote, index) => {
        const position = getRandomPosition();
        return (
          <Paper
            key={index}
            elevation={2}
            sx={{
              position: 'absolute',
              top: position.y,
              left: position.x,
              padding: '20px',
              borderRadius: 2,
              backgroundColor: '#fff3e0',
              boxShadow: 3,
              maxWidth: '250px',
              textAlign: 'center',
            }}
          >
            <Typography variant="body1" sx={{ fontStyle: 'italic', color: '#3e2723' }}>
              {quote}
            </Typography>
          </Paper>
        );
      })}
    </Container>
  );
};

export default HomePage;
