import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

// Set up axios defaults
const instance = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

export const getBooks = () => axios.get(`${API_URL}/books`);
export const addBook = (book) => axios.post(`${API_URL}/books`, book);
export const updateBook = (bookId, updatedBook) => axios.put(`${API_URL}/books/${bookId}`, updatedBook);
export const deleteBook = (bookId) => axios.delete(`${API_URL}/books/${bookId}`);

// Authentication
export const signup = (email, password) => {
  return instance.post('/auth/signup', { email, password });
};

export const login = (email, password) => {
  return instance.post('/auth/login', { email, password });
};

