import React, { useState, useEffect } from 'react';
import { Container, Typography, TextField, Button, Paper, Box, Alert, List, ListItem, ListItemText, IconButton, Modal, Dialog, DialogActions, DialogContent, DialogTitle } from '@mui/material';
import { Edit, Delete, Search, Add } from '@mui/icons-material';
import { getBooks, addBook, updateBook, deleteBook, getRecommendations, getReviews, addReview } from './api'; // Ensure path is correct
import axios from 'axios';

const BooksPage = () => {
  const [books, setBooks] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedBook, setSelectedBook] = useState(null);
  const [showDialog, setShowDialog] = useState(false);
  const [formValues, setFormValues] = useState({ title: '', author: '', description: '' });
  const [error, setError] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [recommendationError, setRecommendationError] = useState('');
  const [readingHistory, setReadingHistory] = useState('');
  const [reviews, setReviews] = useState([]);
  const [reviewError, setReviewError] = useState('');
  const [newReview, setNewReview] = useState('');

  useEffect(() => {
    const fetchAllBooks = async () => {
      try {
        const response = await getBooks();
        setBooks(response.data);
      } catch (error) {
        setError('Failed to fetch books');
      }
    };

    fetchAllBooks();
  }, []);

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const response = await getBooks();
      setBooks(response.data.filter(book => book.title.toLowerCase().includes(searchQuery.toLowerCase())));
    } catch (error) {
      setError('Failed to search books');
    }
  };

  const handleAddOrUpdateBook = async (e) => {
    e.preventDefault();
    try {
      if (selectedBook) {
        await updateBook(selectedBook._id, formValues);
      } else {
        await addBook(formValues);
      }
      setShowDialog(false);
      setFormValues({ title: '', author: '', description: '' });
      setSelectedBook(null);
      // Refresh the book list after adding or updating
      const response = await getBooks();
      setBooks(response.data);
    } catch (error) {
      setError('Failed to add or update book');
    }
  };

  const handleDeleteBook = async (bookId) => {
    try {
      await deleteBook(bookId);
      // Refresh the book list after deletion
      const response = await getBooks();
      setBooks(response.data);
    } catch (error) {
      setError('Failed to delete book');
    }
  };

  const openDialog = (book = null) => {
    if (book) {
      setSelectedBook(book);
      setFormValues({ title: book.title, author: book.author, description: book.description });
    } else {
      setSelectedBook(null);
      setFormValues({ title: '', author: '', description: '' });
    }
    setShowDialog(true);
  };

  const handleGetRecommendations = async () => {
    try {
      const response = await getRecommendations({
        readingHistory: books.map(book => book.title),
      });
      
      setRecommendations(response.data.recommendations);
      setRecommendationError('');
    } catch (error) {
      setRecommendationError('Failed to get recommendations');
    }
  };

  const handleAddReview = async (e) => {
    e.preventDefault();
    try {
      await addReview(selectedBook._id, { content: newReview });
      setNewReview('');
      fetchReviews(selectedBook._id);
    } catch (error) {
      setReviewError('Failed to add review');
    }
  };

  const fetchReviews = async (bookId) => {
    try {
      const response = await getReviews(bookId);
      setReviews(response.data);
    } catch (error) {
      setReviewError('Failed to fetch reviews');
    }
  };


  return (
    <Container maxWidth="md" sx={{ paddingY: '20px' }}>
      
      <Paper sx={{ padding: '40px', marginBottom: '20px' }}>
        <Typography 
          variant="h4" 
          component="h1"
          gutterBottom 
          sx={{ 
            fontWeight: 'bold', 
            color: '#3e2723', 
            fontSize: '2rem', 
            textAlign: 'center', 
            marginBottom: '20px'
          }}
        >
          Books Collection
        </Typography>
        <form onSubmit={handleSearch}>
          <TextField
            label="Search by Title"
            variant="outlined"
            fullWidth
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            sx={{ marginBottom: '10px' }}
          />
          <Box sx={{ display: 'flex', justifyContent: 'center' }}>
            <Button 
              type="submit" 
              variant="contained" 
              color="primary"
              startIcon={<Search />}
              sx={{ paddingX: 5, backgroundColor: '#8d6e63', '&:hover': { backgroundColor: '#6d4c41' } }}
            >
              Search
            </Button>
          </Box>
        </form>
      </Paper>

      {error && <Alert severity="error" sx={{ marginBottom: '20px' }}>{error}</Alert>}

      <Paper sx={{ padding: '40px', marginBottom: '20px' }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Typography 
            variant="h6" 
            gutterBottom
            sx={{ 
              fontWeight: 'bold', 
              color: '#3e2723', 
              fontSize: '1.5rem',
              textAlign: 'center', 
              marginBottom: '20px'
            }}
          >
            Book List
          </Typography>
          <Button 
            variant="contained" 
            color="primary" 
            onClick={() => openDialog()} 
            startIcon={<Add />} // Add "+" icon
            sx={{ paddingX: 5, backgroundColor: '#8d6e63', '&:hover': { backgroundColor: '#6d4c41' } }}
          >
            Add New Book
          </Button>
        </Box>
        <List>
          {books.map((book) => (
            <ListItem key={book._id} divider>
              <ListItemText 
                primary={book.title} 
                secondary={
                  <React.Fragment>
                    <span>Author: {book.author}</span>
                    <br />
                    <span>Description: {book.description}</span>
                  </React.Fragment>
                }
              />
              <IconButton onClick={() => openDialog(book)}>
                <Edit />
              </IconButton>
              <IconButton onClick={() => handleDeleteBook(book._id)}>
                <Delete />
              </IconButton>
            </ListItem>
          ))}
        </List>
      </Paper>

      <Paper sx={{ padding: '40px', marginBottom: '20px' }}>
        <Typography 
          variant="h6" 
          gutterBottom
          sx={{ 
            fontWeight: 'bold', 
            color: '#3e2723', 
            fontSize: '1.5rem', 
            textAlign: 'center', 
            marginBottom: '20px'
          }}
        >
          Get Recommendations
        </Typography>
        <Box sx={{ display: 'flex', justifyContent: 'center' }}>
          
          <Button 
            variant="contained" 
            color="primary" 
            onClick={handleGetRecommendations}
            sx={{ paddingX: 5, backgroundColor: '#8d6e63', '&:hover': { backgroundColor: '#6d4c41' } }}
          >
            Get Recommendations
          </Button>
        </Box>
        {recommendationError && <Alert severity="error" sx={{ marginTop: '30px' }}>{recommendationError}</Alert>}
        {recommendations.length > 0 && (
          <Paper sx={{ padding: '20px', marginTop: '20px' }}>
            <Typography variant="h6" gutterBottom>Recommended Books</Typography>
            <List>
              {recommendations.map((rec, index) => (
                <ListItem key={index} divider>
                  <ListItemText primary={rec} />
                </ListItem>
              ))}
            </List>
          </Paper>
        )}
        <Typography 
          variant="h6" 
          gutterBottom
          sx={{ 
            fontWeight: 'bold', 
            color: '#3e2723', 
            fontSize: '1rem', 
            textAlign: 'center', 
            marginBottom: '20px',
            marginTop: '20px'
          }}
        >
          Or
        </Typography>
        <TextField
            label="Enter keyword to get recommendation"
            variant="outlined"
            multiline
            rows={4}
            fullWidth
            value={readingHistory}
            onChange={(e) => setReadingHistory(e.target.value)}
            sx={{ marginBottom: '10px' }}
          />
      </Paper>

      <Modal
        open={showDialog}
        onClose={() => setShowDialog(false)}
        aria-labelledby="modal-title"
        aria-describedby="modal-description"
      >
        <Paper 
          elevation={4} 
          sx={{ 
            padding: '20px', 
            borderRadius: 2, 
            width: '80%',
            maxWidth: '500px',
            margin: 'auto',
            marginTop: '10%',
          }}
        >
          <DialogTitle id="modal-title">{selectedBook ? 'Update Book' : 'Add New Book'}</DialogTitle>
          <DialogContent>
            <form onSubmit={handleAddOrUpdateBook}>
              <TextField
                label="Title"
                variant="outlined"
                fullWidth
                required
                value={formValues.title}
                onChange={(e) => setFormValues({ ...formValues, title: e.target.value })}
                sx={{ marginBottom: '20px' }}
              />
              <TextField
                label="Author"
                variant="outlined"
                fullWidth
                required
                value={formValues.author}
                onChange={(e) => setFormValues({ ...formValues, author: e.target.value })}
                sx={{ marginBottom: '20px' }}
              />
              <TextField
                label="Description"
                variant="outlined"
                fullWidth
                multiline
                rows={4}
                value={formValues.description}
                onChange={(e) => setFormValues({ ...formValues, description: e.target.value })}
                sx={{ marginBottom: '20px' }}
              />
              <DialogActions>
                <Button 
                  type="submit" 
                  variant="contained" 
                  color="primary"
                >
                  {selectedBook ? 'Update' : 'Add'}
                </Button>
                <Button 
                  onClick={() => setShowDialog(false)}
                  color="secondary"
                >
                  Cancel
                </Button>
              </DialogActions>
            </form>
          </DialogContent>
        </Paper>
      </Modal>

      <Paper sx={{ padding: '20px', marginTop: '20px' }}>
      <TextField
        label="Write a Review"
        variant="outlined"
        fullWidth
        multiline
        rows={4}
        value={reviews}
        onChange={(e) => setReviews(e.target.value)}
        sx={{ marginBottom: '10px' }}
      />
      <Button
        variant="contained"
        color="primary"
        onClick={handleAddReview}
        sx={{ marginBottom: '20px' }}
      >
        Add Review
      </Button>
      {error && <div style={{ color: 'red' }}>{error}</div>}
      <List>
        {reviews.map((review) => (
          <ListItem key={review._id} divider>
            <ListItemText primary={review.text} />
          </ListItem>
        ))}
      </List>
    </Paper>
    </Container>
  );
};

export default BooksPage;
